function chgUSCat(strzone){
      if (strzone!='0') {
          var GeoUSDFP = {};
          GeoJson = JSON.parse($.cookie('GeoDFP'));
          if(GeoJson){
            GeoUSDFP['CC'] = GeoJson['DFP']['CC'];
            GeoUSDFP['S'] = GeoJson['DFP']['S'];
            switch(strzone) {
                case 'la' :
                    GeoUSDFP['D'] = '803';
                    break;
                case 'sf' :
                    GeoUSDFP['D'] = '807';
                    break;
                case 'ny' :
                    GeoUSDFP['D'] = '501';
                    break;
            }

            $.cookie('GeoDFP', JSON.stringify({"DFP":GeoUSDFP}),{expires: 7, path: '/' });
            location.href = "/realtimenews/section/"+strzone;
          }
      }
}

