var $window = $(window);
var $extras = $('#extras');
var $corpfoot = $('#corpfoot');
var $door = $('#door');
var $doorLeft = $('#door-left');
var $doorRight = $('#door-right');
var doorMarginTop = ($door.css('margin-top')) ? parseInt($door.css('margin-top').replace('px',''), 10) : 0;
var scrollTargetTop = 130;
$(window).scroll(function() {
  var bottomPos;
  if ($extras.length && $corpfoot.length) {
    bottomPos = Math.min($extras.offset().top, $corpfoot.offset().top);
  } else if (!$extras.length && !$corpfoot.length) {
    bottomPos = $(document).height();
  } else {
    bottomPos = ($extras.offset() || $corpfoot.offset()).top;
  }

  var scrollTop = $window.scrollTop();
  var bannerHeight = Math.max($doorLeft.height(),$doorRight.height());
  var checkPoint = scrollTop+bannerHeight;

  if ((scrollTop>scrollTargetTop) && (checkPoint < bottomPos)) {
    $door.css('position','fixed').css('top','-'+doorMarginTop+'px');
  } else {
    if (checkPoint >= bottomPos) {
      $door.css('position','absolute').css('top',(bottomPos-bannerHeight-doorMarginTop)+'px');
    } else {
      $door.css('position','relative').css('top','0px');
    }
  }
}).trigger('scroll');

